  const token = useLoaderData();
  // const navigation = useNavigation();
  useEffect(() => {
    if (!token) {
      return;
    }

    setTimeout(() => {
      useSubmit();
    });
  }, [token]);

  return (
    <>
      <MainNavigation />
      <main>
        {/* {navigation.state === 'loading' && <p>Loading...</p>} */}
        <Outlet />
      </main>
    </>
  );
}

export default RootLayout;
